/* current issues: background image (mobile), site deployment */

@charset "UTF-8";

// Configuration (variables, functions, etc)
@use
  './config' as *;

// import resets
@use 
  './reset' as *;


// Raleway and Open Sans fonts
@import url('https://fonts.googleapis.com/css2?family=Open+Sans:wght@400;500;600;700&family=Raleway:wght@400;500;600;700&display=swap');

body {
  background-color: $dark-blue-2;
  color: $neutral;
  /* Open Sans font */
  font-family: $font-2;
  font-weight: weight('regular');
  /* base font-size is 14px */
  font-size: $base-size;
  overflow-x: hidden;
}


.header__links,
h1,
h2,
h3,
h4,
h5,
h6,
.btn {
  /* raleway font for headers and headings */
  font-family: $font-1;
}

.header {
  background-color: $dark-blue-1;
  @include docSidePadding;
  padding-top: 2em;
  padding-bottom: 2.25em;

  &__logo {
    width: 6.2em;
  }

  &__links {
    float: right;
  }

  &__link {
    display: inline-block;
    font-size: 0.75rem;
    margin-left: 1.2em;
  }

  a, a:visited {
    color: $neutral;
  }

  a:hover {
    font-weight: weight('semi-bold');
    text-decoration: underline;
  }
}

/* styles for hero section */
.hero {
  text-align: center;
  margin-bottom: 8em;
  @include docSidePadding;
  padding-top: 1em;
  background: url('../assets/images/bg-curvy-mobile.svg') no-repeat bottom;
  background-size: contain;
  background-color: $dark-blue-1;

  &__img {
    margin-bottom: 1.75em;
  }

  &__title {
    margin-bottom: .65em;
    font-size: 1.3rem;
    font-weight: weight('semi-bold');
    line-height: 1.6;
  }

  &__desc {
    margin-bottom: 2em;
  }

  /* styles for main buttons */
  .btn--primary {
    width: 77%;
    background: $accent-gradient;
    border: none;
    color: $neutral;
    margin-bottom: 2em;
    padding: 1.1em 0;
    border-radius: 2em;
    font-size: 0.8rem;
    font-weight: weight('semi-bold');

    &:hover {
      cursor: pointer;
      background: lighten($accent-1, 10%);
    }
  }
}

/* styles for feature section */
.features {
  text-align: center;
  @include docSidePadding;
  margin-bottom: 10em;

  .feature {
    &__img {
      margin-bottom: 1.75em;
      width: 4.2em;
    }

    &__title {
      margin-bottom: 0.7em;
      font-size: 1.1rem;
      font-weight: weight('semi-bold');
    }

    &__desc {
      margin-bottom: 6em;
    }
  }
}

/* styles for call-to-action section */
.c2a {
  margin-bottom: 8em;
  @include docSidePadding;

  .wrapper {
    display: flex;
    flex-direction: column;
  }

  &__img {
    align-self: center;
  }

  &__content {
    margin-top: 2.5em;
  }

  &__title {
    font-size: 1.05rem;
    margin-bottom: 1em;
    margin-left: 0.25em;
    font-weight: weight('semi-bold');
  }

  .btn--secondary {
    padding: 0 0 0.4em;
    color: $accent-1;
    background: transparent;
    border: none;
    border-bottom: 0.1em solid;

    i {
      position: relative;
      top: 0.28em;
      margin-left: 0.25em;
      // font-size: 3rem;
      &:hover {
        // filter: invert(27%) sepia(51%) saturate(2878%) hue-rotate(346deg) brightness(104%) contrast(97%);
      }

    }

    img {
      color: $neutral;
      // filter: invert(0%) sepia(0%) saturate(0%) hue-rotate(10deg) brightness(50%) contrast(100%);
      // filter: brightness(0) saturate(100%) invert(96%) sepia(1%) saturate(2%) hue-rotate(356deg) brightness(106%) contrast(0%);
    }



    &:hover {
      color: $neutral;
      cursor: pointer;
      // filter: brightness(100) saturate(0%);
      // filter: brightness(0) saturate(100%) invert(96%) sepia(1%) saturate(2%) hue-rotate(356deg) brightness(106%) contrast(100%);

    }
  }
}

/* styles for testimonial section */
.testimonials {
  padding-top: 1.5em;

  .wrapper {
      /* pseudo class for placing quote background */
    &::before {
      background: url('../assets/images/bg-quotes.png');
      background-repeat: no-repeat;
      content: '';
      position: absolute;
      height: 2em;
      width: 100%;
      background-size: 1.5em;
      left: 3em;
