    }

    /* styles for email submit button */
    [type=submit] {
      background: $accent-gradient;
      color: $neutral;
      font-weight: weight('semi-bold');
      margin-top: 1.75em;
    }
  }
}

/* styles for footer section */  
 .footer {
   @include docSidePadding;
   background-color: $dark-blue-3;
   padding-bottom: 2.5em;

   &__logo {
     width: 12em;
     padding-top: 4em;
     margin: 0 1em 2.25em 0.75em;
   }

   &__desc {
     margin-bottom: 1.25em;

     i {
        float: left;

        img {
          width: 1em;
        }
      }

      p {
        padding-left: 3em;
      }
    }

    &__link {
      margin-bottom: 1.25em;

      i {
        float: left;
        padding-right: 2em;

        img {
          width: 1em;
        }
      }
    }

    .column {
      &__part {
        &:nth-child(1), &:nth-child(2){
          i {
            padding-top: 0.3em;
          }
        }
      }

      &:nth-child(3) {
        i {
          padding-top: 0.3em;
        }
      }
      &:nth-child(3) {
        margin-bottom: 4.5em;
      }

      &:nth-child(4) {
        margin-bottom: 3.5em;
      }

      &:nth-child(5) {
        margin-bottom: 4.5em;
      }

      &:nth-child(6) {
        ul {
          text-align: center;
        }
        li {
          display: inline-block;
          margin-right: 0.5em;
          padding: 0.3em 0;
          width: 2em;
          border: 0.01em solid $neutral;
          border-radius: 50%;
        }
      }
    }
  }

/* Adjust for larger screen size */
@media (min-width: map-get($breakpoints, 'medium')) {
  .header {
    padding: 5.5em 6em;
    &__logo {
      width: 12.5em;
    }

    &__link {
      font-size: 1rem;
      margin-left: 3.5em;
    }
  }

  .hero {
    background: url('../assets/images/bg-curvy-desktop.svg') no-repeat bottom;
    background-size: contain;
    background-color: hsl(217deg, 28%, 15%);
    padding-left: 0;
    padding-right: 0;

    &__title {
      width: 52%;
      font-size: 2.5rem;
      line-height: 1.45;
      margin: 0 auto;
      margin-top: 0.25em;
    }

    &__desc {
      font-size: 1.2rem;
      width: 57%;
      margin: 1.5em auto;
      margin-bottom: 1.75em;
      padding: 0 5em;
    }

    .btn--primary {
      width: 21%;
      font-size: 1rem;
      padding: 1.2em 0em;
    }
  }

  .features {
    padding: 0;
    margin-bottom: 5em;

    .wrapper {
      display: grid;
      grid-template-columns: 1fr 2fr 2fr 1fr;
      grid-column-gap: 1em;
    }
    .feature {  
      &__img {
        height: 5.5em;
        width: auto;
      }

      &__title {
        font-size: 1.25rem;
      }

      &__desc {
        padding: 0 3em;
      }

      &:nth-child(1) {
        grid-column: 2;
      }

      &:nth-child(2) {
        grid-column: 3;
      }

      &:nth-child(3) {
        grid-column: 2;
      }

      &:nth-child(4) {
        grid-column: 3;
      }
    }
  }

  .c2a {
    padding: 0 8em;

    .wrapper {
      flex-direction: row;
      align-items: center;
    }

    &__img {
      min-width: 50%;
    }

    &__content {
      margin-top: 0;
      margin-left: 4em;
    }

    &__title {
      font-size: 2.5rem;
      margin-left: 0;
      margin-bottom: 0.6em;
      padding-right: 1.5em;
    }

    &__desc {
      font-size: 1rem;
      line-height: 1.45;
    }

    &__btn {
      font-family: $font-2;
      font-size: 1rem;
      margin-top: 0.75em;
    }
  }

  .testimonials {
    margin-bottom: 2em;

    .wrapper {
      display: grid;
      grid-template-columns: 0.25fr 1fr 1fr 1fr 0.25fr;
      /* grid-column-gap is depracated, but including it to cover more cases */
      grid-column-gap: 3em;
      column-gap: 3em;
      position: relative;

      &::before {
        grid-column: 2;
        height: 6em;
        top: -2.6em;
        left: -0.7em;
        background-size: 4em;
      }
    }

    .testimonial {
      padding: 2.5em 2em 1.75em;
      box-shadow: 2px 2px 4px $dark-blue-4;

      &__desc {
        font-size: 0.9rem;
        line-height: 1.5;
        margin-bottom: 1.75em;
      }

      &__author {
        line-height: 1.05;
      }

      .author__avatar {
        width: 2em;
      }

      .author__name {
        font-size: 0.7rem;
      }

      .author__desc {
        font-size: 0.5rem;
      }

      &:nth-child(1) {
        grid-column: 2;
        margin: 0;
      }

      &:nth-child(2) {
        grid-column: 3;
        margin: 0;
      }

      &:nth-child(3) {
        grid-column: 4;
        margin: 0;
      }
    }
  }

  .newsletter {
    height: 10em;
    background-color: $dark-blue-2;
    margin: 0;
    margin-top: 14em;
    padding: 0;
    border-radius: 0;

    &__title {
      font-size: 2rem;
      margin-bottom: 0.75em;    
    }

    &__desc {
      font-size: 0.9rem;
      padding: 0 1em 0.75em;
      line-height: 1.5;
    }

    &__form {
      display: flex;
      gap: 2em;

      [type=email] {
        flex: 2;
        padding: 1.6em 3em;
        border-radius: 3em;
      }

      ::placeholder {
        font-size: 0.8rem;
      }

      [type=submit] {
        flex: 1;
        margin-top: 0;
        padding: 1em 0.5em;
        font-family: $font-1;
        font-size: 0.9rem;
      }
    }

    .wrapper {
      background-color: $dark-blue-1;
      box-shadow: 0.5em 0.6em 0.5em rgba(0, 0, 0, 0.2);      
      margin: 10em 1.65em 1em;
      padding: 3em 5em 2.5em;      
      border-radius: .2rem;
      width: 60%;
      margin: 0 auto;
      position: relative;
      z-index: 1;
    }
  }

  .footer {
    padding-top: 14em;
    padding-left: 0;
    padding-right: 0;

    &__logo {
      width: auto;
      margin-bottom: 1.25em;
      padding: 0;
    }

    &__desc {
      font-size: 0.94rem;
      line-height: 1.5;

      i {
        img { 
          width: 0.85em;
        }
      }

      p {
        padding-left: 2.6em;
      }
    }

    &__link {
      margin-bottom: 1.2em;

      i {
        padding-right: 1.5em;

        img {
          width: 1.25em; 
        }     
      }
    }

    .columns {
      display: grid;
      grid-template-columns: 8% 25% repeat(4, auto); 
      grid-gap: 0.5em;   
    }

    .column {
      margin-bottom: 0;

      &__part {
        &:nth-child(1) {
          grid-row: 1 / 2;
          grid-column: 2 / 3;
        }

        &:nth-child(2) {
          grid-row: 2 / 3;
          grid-column: 2 / 3;
          margin-bottom: 0;
        }
      }

      &:nth-child(3) {
        grid-row: 2 / 3;
        grid-column: 3 / 4;
        margin-bottom: 0;
        i {
          padding-top: 0;
        }
        justify-self: center;
      }

      &:nth-child(4) {
        grid-row: 2 / 3;
        grid-column: 4 / 5;
        margin-bottom: 0;
        justify-self: center;

        ul {
          font-size: 1rem;
        }
      }

      &:nth-child(5) {
        grid-row: 2 / 3;
        grid-column: 5 / 6;
        justify-self: center;

        ul {
          font-size: 1rem;
        }
      }

      &:nth-child(6) {
        grid-row: 2 / 3;
        grid-column: 6 / 7;

        li {
          margin-right: 0.6em;
        }
      }
    }
  }
}


  /**
 * Clear inner floats
 */
.clearfix::after {
  clear: both;
  content: '';
  display: table;
  overflow: hidden;
}

/**
 * Hide element while making it readable for screen readers
 * Shamelessly borrowed from HTML5Boilerplate:
 * https://github.com/h5bp/html5-boilerplate/blob/master/src/css/main.css#L119-L133
 */
.visually-hidden {
  border: 0;
  clip: rect(0 0 0 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}

